namespace SubstituteQueryable{	using System.Linq;	using System.Linq.Expressions;	using Remotion.Linq;	public class SubstituteQueryable<T> : QueryableBase<T>	{		public SubstituteQueryable(params T[] items) : this((items ?? Enumerable.Empty<T>()).AsQueryable())		{		}		public SubstituteQueryable(IQueryable<T> items) : base(new SubstituteQueryProvider<T>(items))		{		}		internal SubstituteQueryable(IQueryProvider provider, Expression expression) : base(provider, expression)		{		}	}}